mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, image = #{image}, price = #{price}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select d.*,c.name as categoryName
from dish d left outer join category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and d.status = #{status}


order by d.create_time desc
